        public static string LoadTemplate()
        {
            string result = default(string);
            try
            {
                StringBuilder stringBuilder = new StringBuilder();
                stringBuilder.Append(LoadPopupTemplate()).Append("\r\n");
                stringBuilder.Append(LoadChatTemplate()).Append("\r\n");
                stringBuilder.Append(LoadChatTemplate2()).Append("\r\n");
                stringBuilder.Append(LoadListTemplate()).Append("\r\n");
                result = stringBuilder.ToString();
                return result;
            }
            catch (Exception ex)
            {
                ProjectData.SetProjectError(ex);
                Exception ex2 = ex;
                ProjectData.ClearProjectError();
                return result;
            }
        }

